def santa2(s)
	l = s.length
	lvl=0
	(1..l).each do |i|
		s.byteslice(i-1) == "(" ? lvl+=1 : lvl-=1
		if lvl== -1
			puts i
			exit
		end
	end
	puts lvl
end

